import { Component,Input,Output,EventEmitter } from '@angular/core';
import { products } from '../products';

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent {
  products = [...products];
@Input( )selectedProduct:string |undefined;
  share() {
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }
}
<div *ngFor="let product of products">
    <div *ngIf="product.category==selectedProduct">
    <h3>
       <a [title]="product.name + ' details'">
          <!-- [routerLink]="['/products',product.id]"> -->
          {{ product.name }}
        </a>
    </h3>
    <img src={{product.image}} alt="image" style="width: 20%; height:20%;">
    <p *ngIf="product.price">Price:{{product.price}}</p>
    <p *ngIf="product.rating">Rating:{{product.rating}}</p>
    <p *ngIf="product.description"> Description: {{ product.description }}</p>

    <button type="button"(click)="like(product)">Like|</button>
    <a href="https://t.me/share/url?url={{product.url}}">

    <button type="button" (click)="share()">
    Share
    </button> </a>

    <app-product-alerts
    [product]="product" 
    (notify)="onNotify()">
    </app-product-alerts>
</div>    
